import React from 'react';
import { DragDropContext } from "react-beautiful-dnd";

export default function Lists({ todoData, setTodoData }) {

  const handleClick = (id) => {
    let newTodoData = todoData.filter(data => data.id !== id)
    setTodoData(newTodoData);
  }

  const handleCompleteChange = (id) => {
    let newTodoData = todoData.map((data) => {
      if(data.id === id) {
        data.completed = !data.completed;
      }
      return data;
    });

    setTodoData(newTodoData);
  };

  return (
    <div>
      <DragDropContext onDragEnd={handle}>
        <Droppable droppableId="todo">
          {(provided) => (
            <div {...provided.droppableProps} ref={provided.innerRef}>
              {todoData.map((data, index) => (
                <Draggable
                  key={data.id}
                  DraggableId={data.id.toString()}
                  index={index}
                >
                  {(provided, snapshot) => (
                    <div 
                      className={`${snapshot.isDragging ? "bg-grey-400" : "bg-grey-100"} flex items-center justify-between w-full px-4 py-1 my-2 text-gray-600 bg-gray-100 border rounded`}
                      key={data.id} {...provided.draggableProps} 
                      ref={provided.innerRef} {...provided.dragHandleProps}>
                      <div className='items-center'>
                        <input 
                          type="checkbox" 
                          defaultChecked={data.completed} 
                          onChange={() => handleCompleteChange(data.id)} /> {" "}
                        <span className={ data.completed ? "line-through" : undefined }>{data.title}</span>
                      </div>
                      <div>
                        <button className='px-4 py-2 float-right' onClick={() => handleClick(data.id)}>X</button>
                      </div>
                    </div>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </div>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  )
}
